import { APIPayload } from '../../common/v2.js';
import '../../utils.js';

/**
 * APIListedFile#type
 * @see https://docs.squarecloud.app/api-reference/endpoint/apps/filemanager/list
 */
type FileType = "file" | "directory";
declare const FileType: {
    readonly File: "file";
    readonly Directory: "directory";
};
/**
 * @see https://docs.squarecloud.app/api-reference/endpoint/apps/filemanager/list
 */
interface APIListedFile {
    type: FileType;
    name: string;
    size: number;
    lastModified: number;
}
type APIFileListPayload = APIPayload<APIListedFile[]>;
/**
 * @see https://docs.squarecloud.app/api-reference/endpoint/apps/filemanager/read
 */
interface APIReadFile {
    type: string;
    data: number[];
}
type APIFileReadPayload = APIPayload<APIReadFile>;

export { type APIFileListPayload, type APIFileReadPayload, type APIListedFile, type APIReadFile, FileType };
